// some array stuff we want

// uniquify contents
Array.prototype.unique = function( ) {

    if( this == null ) {
        throw new TypeError( "this is null or not defined" );
    }

    var h = { };
    var r = [ ];
    var v;

    for( var i = 0; i < this.length; i++ ) {
        v = this[i];
        if( !( v in h ) ) {
            r.push( v );
        }
        h[v] = true;
    }

    return r.sort( );
};


// simple indexOf copy with true/false
Array.prototype.contains = function( val ) {

    for( var i = 0; i < this.length; i++ ) {
        if( this[i] === val ) {
            return true;
        }
    }

    return false;
};


// slice an array for elements with index i = num (mod size)
Array.prototype.modulo = function( num, size ) {

    var ret = [ ];

    for( var i = 0; i < this.length; i++ ) {
        if( ( i % size ) === num ) {
            ret.push( this[i] );
        }
    }

    return ret;
};



// remove all copies of an element from an array, preserve the order
Array.prototype.without = function( element ) {

    var ret = [ ];

    for( var i = 0; i < this.length; i++ ) {
        if( this[i] !== element ) {
            ret.push( this[i] );
        }
    }

    return ret;
};



