// 32-bit int hash based on string contents
// Derived from a DJB hash
String.prototype.hashVal = function( ) {

    var hash = 5381;
    var cval;

    for( var i = 0; i < this.length; i++ ) {
        cval  = this.charCodeAt( i );
        hash += (hash << 5) + cval;
        hash  = hash & hash;
    }

    return ( hash < 0 ) ? -hash : hash;
}

